void prepare_token(int category)
{
    yytoken = token_new(category, yylineno, yytext,
    (const char *)list_back(yyfiles));
    struct node *n = node_new(TOKEN);
    n->token = yytoken;
    yylval.nodeptr = tree_new(NULL, n, NULL,
    (void (*)(void *, bool))&delete_tree);
}


