void handle_include()
{
	
    char *fname = malloc(strlen(yytext)+1-strlen("#include \"\""));
    fname = strchr(yytext, '\"')+1;
    fname[strlen(fname)-1] = '\0';
    printf("%s\n",fname);
    yyin = fopen(fname,"r");
    push_node(&filestack, fname);
    yypush_buffer_state(yy_create_buffer(yyin, YY_BUF_SIZE));

}


