static int check_identifier( char *s, tableptr t ) {
    
    int *c = hasht_search(yytypes, (void *)s);
    if (c)
    T(*c);
    else
    T(IDENTIFIER);
};

void delete_tree(void *data, bool leaf)
{
    struct node *n = data;
    if (n->token)
    token_free(n->token);
    free(data);
}

